        return Doctrine_Core::getTable('Order_Model_Doctrine_Order');
    }
    
    public function getFullOrderQuery() {
        $q = $this->createQuery('o')
                ->addSelect('o.*')
                ->addSelect('u.*')
                ->addSelect('pr.*')
                ->addSelect('i.*')
                ->addSelect('d.*')
                ->addSelect('da.*')
