                ->leftJoin('o.Delivery d')
                ->leftJoin('d.DeliveryAddress da')
                ->leftJoin('o.User u')
                ->leftJoin('u.Profile pr')
                ->leftJoin('o.Items i')
                ->leftJoin('i.Product prod')
                ->leftJoin('prod.Translation tr')
                ;
        return $q;
    }
}